SELECT ?character ?characterLabel ?imgself ?imgactor ?killerLabel ?methodLabel ?genusLabel ?placeLabel ?episodeLabel WHERE {
  ?character wdt:P1080 wd:Q2461698.
  OPTIONAL { ?character wdt:P509 ?method. }
  OPTIONAL { ?character wdt:P157 ?killer. }
  OPTIONAL { ?character wdt:P175 ?actor.
            ?actor wdt:P18 ?imgactor.}
  OPTIONAL {?character wdt:P18 ?imgself.}
  ?character p:P1196 ?statement.
  ?statement ps:P1196 ?genus.
  OPTIONAL {?character wdt:P20 ?place}
  OPTIONAL {?statement  prov:wasDerivedFrom [ pr:P248 ?episode ].}
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en,[AUTO_LANGUAGE],en". }
}
ORDER BY ?dod

#defaultView:Timeline
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX skos: <http://www.w3.org/2004/02/skos/core#>
PREFIX dbc: <http://dbpedia.org/resource/Category:>

SELECT DISTINCT  ?wditem ?wditemLabel ?date ?image ?work ?workLabel WHERE {  
    SERVICE <http://dbpedia.org/sparql> {
        ?item dct:subject|dct:subject/skos:broader dbc:History_of_telecommunications ;
              owl:sameAs ?wditem FILTER regex (?wditem, "wikidata.org") .
    }        
    SERVICE <https://query.wikidata.org/sparql> {
	  {?wditem wdt:P569 ?date FILTER (YEAR(?date)>1700).}
      UNION
      {?wditem wdt:P571|wdt:P577|wdt:P580 ?date .}
      OPTIONAL {?wditem wdt:P800 ?work. ?work rdfs:label ?workLabel FILTER (LANG(?workLabel)="en") .}
      OPTIONAL {?wditem wdt:P18 ?image } .  
      ?wditem rdfs:label ?wditemLabel FILTER (LANG(?wditemLabel)="en") .
    }
}

#defaultView:Graph
# by @piecesofuk 16th April 2019
SELECT DISTINCT ?line ?lineLabel ?rgb ?p ?pLabel WHERE {
  VALUES $IS_ON_LINE { wdt:P81 }
  VALUES $IS_PART_OF { wdt:P361 }
  VALUES $LONDON_UNDERGROUND { wd:Q20075 }

  ?p $IS_ON_LINE ?line .
  ?line $IS_PART_OF $LONDON_UNDERGROUND .
  ?line wdt:P465 ?rgb . # gets the tube line's colour
  { # inner select returns all stations which are on 3 or more London Underground lines
    SELECT ?p (COUNT(?line) AS ?count) WHERE {
      VALUES $IS_A { wdt:P31 }
      VALUES $TUBE_STATION { wd:Q14562709 }
      VALUES $IS_ON_LINE { wdt:P81 }
      VALUES $IS_PART_OF { wdt:P361 }
      VALUES $LONDON_UNDERGROUND { wd:Q20075 }

      ?p $IS_A $TUBE_STATION .
      ?p $IS_ON_LINE ?line .
      ?line $IS_PART_OF $LONDON_UNDERGROUND .
    } GROUP BY ?p
    HAVING (?count >= 3)
  }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

