#Map of places of residence of accused witches.
#defaultView:Map

#You need to use the name ?layer as the variable you are colour coding by 
#It's much easier to change later if you do this in the SELECT area, using e.g. (?Gender as ?layer)

SELECT ?person ?personLabel ?accusedwitch ?precision ?coords ?Occupation ?Residence ?image (?Occupation as ?layer) 
WHERE {
  ?person wdt:P4478 ?accusedwitch .
  ?person wdt:P31 wd:Q5.
  ?person wdt:P21 ?gender .
  ?person wdt:P551 ?residence .
  OPTIONAL { ?person wdt:P106 ?occupation} 
  OPTIONAL { ?person wdt:P3716 ?socialclassification }
  ?residence wdt:P625 ?coords.
  OPTIONAL { ?article schema:about ?person. }
  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "en".
    ?person rdfs:label ?personLabel.
    ?residence rdfs:label ?Residence .
    ?occupation rdfs:label ?Occupation.
    ?gender rdfs:label ?Gender.
    ?socialclassification rdfs:label ?SocialClassification .
  }
}

#defaultView:Map
# map of items that have some relation to Saint George
SELECT ?thing ?label ?img ?loc ?layer ?propertyLabel WHERE {
  VALUES $ST_GEORGE { wd:Q48438 }
  ?thing ?p $ST_GEORGE .
  ?thing wdt:P18 ?img .
  ?thing wdt:P625 ?loc .
  ?property wikibase:directClaim ?p . # needed to get property labels
  # does the item have English labels?
  OPTIONAL { 
    ?thing rdfs:label ?label
    FILTER (LANG(?label) = "en")
  }
  BIND(IF(BOUND(?label), "Items with English Labels", "Items without English Labels") AS ?layer)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

#Partigiani Italiani by death place
#defaultView:Map{"layer":"?year"}
SELECT ?item ?itemLabel ?_coordinates ?_image ?death_placeLabel (year(?death_date) as ?year) WHERE {
  ?item wdt:P106 wd:Q212948;   
        wdt:P20/wdt:P17 wd:Q38;
        wdt:P20/wdt:P625 ?_coordinates;   # their deathplace, specifically the coordinates of their birthplace
        wdt:P20 ?death_place;
        p:P570/psv:P570 ?death_date_node .
        ?death_date_node wikibase:timeValue ?death_date .
        OPTIONAL { ?item wdt:P18 ?_image }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en" }  # labels in English
}

SELECT ?count ?typeLabel ?example ?exampleLabel ?exampleDescription ?afterLabel ?afterDescription WHERE {
  {
    select distinct ?type (count(distinct ?item) as ?count) (SAMPLE(?item) AS ?example) WHERE {
        ?item wdt:P138 ?namesake . ?namesake wdt:P31 ?type . 
        ?item wdt:P31 wd:Q5 .
    } group by ?type 
  }
  ?example wdt:P138 ?after. ?after wdt:P31 ?type .
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} order by desc(?count)

