#defaultView:Map{"hide":["?pobCoords", "?conCoords",  "?line", "?rgb"]}
SELECT ?mp ?mpLabel ?image ?pob ?pobLabel ?pobCoords ?con ?conLabel ?conCoords ?line ?rgb ?layer WHERE {
  $mp wdt:P19 ?pob .
  OPTIONAL { ?mp wdt:P18 ?image .}
  ?pob wdt:P625 ?pobCoords .
  ?pob p:P625 [ ps:P625 [];  psv:P625 [ wikibase:geoLongitude ?poblon; wikibase:geoLatitude  ?poblat; ] ].
  $mp p:P39 ?mpstmt .
  ?mpstmt ps:P39 wd:Q30524710 .
  ?mpstmt pq:P768 ?con .  
  ?con wdt:P625 ?conCoords .
  ?con p:P625 [
            ps:P625 [];
            psv:P625 [
               wikibase:geoLongitude ?conlon;
               wikibase:geoLatitude  ?conlat; 
               ]
          ].
  MINUS {?mpstmt pq:P582 ?e } 
  ?mpstmt pq:P4100 ?party .
  ?party wdt:P465 ?rgb .
  ?party rdfs:label ?layer .
  FILTER (LANG(?layer) = "en")
  BIND(CONCAT('LINESTRING (', STR(?poblon), ' ', STR(?poblat), ',', STR(?conlon), ' ', STR(?conlat), ')') AS ?str) .
  BIND(STRDT(?str, geo:wktLiteral) AS ?line) 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

SELECT DISTINCT ?person ?personLabel ?born ?bornLabel ?coord {
  ?position wdt:P279 wd:Q1711695 .           # 1. find all the MSP positions
  ?person wdt:P39 ?position .                # 2. did they hold any MSP position
  ?person wdt:P31 wd:Q5 .                    # 2a. check they were actually people (in case we have any fictional MSPs)
  ?person wdt:P19 ?born .                    # 3. where were they born
  MINUS { ?born wdt:P131* wd:Q22 . }         # 4. this bit filters down to just "not in Scotland"
  OPTIONAL { ?born wdt:P625 ?coord . }       # 5. coordinates if available
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en" . }
}
