#defaultView:BarChart
SELECT ?countryFemale ?countryFemaleLabel (?countFemales / (?countMales + ?countFemales) AS ?ratioFemales) WHERE {
  {
    SELECT ?countryMale (COUNT(?itemMale) AS ?countMales) WHERE {
      ?itemMale wdt:P106 wd:Q82955;
        wdt:P569 ?dateBirthMale;
        wdt:P31 wd:Q5;
        wdt:P21 wd:Q6581097;
        wdt:P27 ?countryMale.
      FILTER((YEAR(?dateBirthMale)) > 1960 )
    }
    GROUP BY ?countryMale
  }
  {
    SELECT ?countryFemale (COUNT(?itemFemale) AS ?countFemales) WHERE {
      ?itemFemale wdt:P106 wd:Q82955;
        wdt:P569 ?dateBirthFemale;
        wdt:P31 wd:Q5;
        wdt:P21 wd:Q6581072;
        wdt:P27 ?countryFemale.
      FILTER((YEAR(?dateBirthFemale)) > 1960 )
    }
    GROUP BY ?countryFemale
  }
  FILTER(?countryMale = ?countryFemale)
  FILTER((?countMales + ?countFemales) > 300 )
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC (?ratioFemales)

