  ?female_authors ?male_authors
  ?event ?eventLabel ?eventDescription
{
  {
SELECT
  ?event
  (COUNT(DISTINCT ?male_organizer) AS ?male_organizers) 
  (COUNT(DISTINCT ?female_organizer) AS ?female_organizers) 
  (COUNT(DISTINCT ?male_author) AS ?male_authors) 
  (COUNT(DISTINCT ?female_author) AS ?female_authors) 
{
  VALUES ?event_type { wd:Q2020153 wd:Q40444998 }
  ?event wdt:P31 ?event_type .
