  }
  {
    ?work wdt:P1433 ?proceedings ; wdt:P50 ?male_author .
    ?male_author wdt:P21 wd:Q6581097 .
  }
  UNION
  {
    ?work wdt:P1433 ?proceedings ; wdt:P50 ?female_author .
    ?female_author wdt:P21 wd:Q6581072 .
  }
}
GROUP BY ?event 
  }
