#Autrices de langue françaises mortes avant 1949
SELECT ?autrice ?autriceLabel ?lieuNaissance ?lieuNaissanceLabel ?lieuMort ?lieuMortLabel (MIN(?img) AS ?image) ?prenomLabel (MIN(?idIsni) as ?isni) ?idBnf (MIN(?n) AS ?naissance) (MAX(?m) AS ?mort) WHERE {
  ?autrice wdt:P106 wd:Q36180.
  ?autrice wdt:P21 wd:Q6581072.
  ?autrice wdt:P570 ?m.
  { ?autrice wdt:P1412 wd:Q150. }
  UNION
  { ?autrice wdt:P1412 wd:Q1473289. }
  OPTIONAL { ?autrice wdt:P569 ?n.}
  OPTIONAL { ?autrice wdt:P19 ?lieuNaissance.}
  OPTIONAL { ?autrice wdt:P20 ?lieuMort.}
  OPTIONAL { ?autrice wdt:P18 ?img. }
  OPTIONAL { ?autrice wdt:P735 ?prenom. }
  OPTIONAL { ?autrice wdt:P213 ?idIsni. }
  OPTIONAL { ?autrice wdt:P268 ?idBnf. }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],fr,en". }
  FILTER((YEAR(?m)) < 1949)
}
GROUP BY ?autrice ?autriceLabel ?lieuNaissance ?lieuNaissanceLabel ?lieuMort ?lieuMortLabel ?image ?prenomLabel ?isni ?idBnf ?mort ?naissance
