# same-sex married couples where neither partner has a sexual orientation
SELECT DISTINCT ?person1 ?person2 ?person1Label ?person1Description ?person2Label ?person2Description WHERE {
  ?person1 wdt:P31 wd:Q5;
           wdt:P21 ?gender;
           wdt:P569 ?dob1;
           wdt:P26 ?person2.
  ?person2 wdt:P31 wd:Q5;
           wdt:P569 ?dob2;
           wdt:P21 ?gender.
  MINUS { ?person1 wdt:P91 ?orientation. }
  MINUS { ?person2 wdt:P91 ?orientation. }
  # arbitrarily filter out some early results, we’re more likely to find references for recent people
  FILTER(?dob1 >= "1950-01-01"^^xsd:dateTime)
  FILTER(?dob2 >= "1950-01-01"^^xsd:dateTime)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

SELECT ?competition ?competitionLabel ?date ?end WHERE {
  { ?competition wdt:P31 wd:Q2990963. }
  UNION
  {
    ?competition wdt:P31 wd:Q16510064.
    ?competition wdt:P361 ?comp.
    ?comp wdt:P31 wd:Q2990963.
  }
  UNION
  {
    ?competition wdt:P361 ?segment.
    ?segment wdt:P31 wd:Q16510064.
  }
  OPTIONAL { ?competition wdt:P580 ?start. }
