#defaultView:Map{"hide":["?clCoord", "?blCoord", "?line"]}
#linking central libraries in California to their branch library 
SELECT ?libSystem ?libSystemLabel ?centralLibrary ?centralLibraryLabel ?clCoord ?branchLibrary ?branchLibraryLabel ?blCoord ?line ?layer WHERE {
  ?libSystem wdt:P31/wdt:P279* wd:Q28324850 ; wdt:P131 ?loc ;
                 wdt:P527 ?centralLibrary ;
                 wdt:P527 ?branchLibrary .
  ?centralLibrary wdt:P31 wd:Q12317349 ;
                  wdt:P625 ?clCoord .
  ?branchLibrary wdt:P31 wd:Q11396180 ; 
                 wdt:P625 ?blCoord .

  ?centralLibrary p:P625 [ ps:P625 [];  psv:P625 [ wikibase:geoLongitude ?clLon; wikibase:geoLatitude ?clLat; ] ].
  ?branchLibrary p:P625 [ ps:P625 [];  psv:P625 [ wikibase:geoLongitude ?blLon; wikibase:geoLatitude ?blLat; ] ].

  BIND(CONCAT('LINESTRING (', STR(?clLon), ' ', STR(?clLat), ',', STR(?blLon), ' ', STR(?blLat), ')') AS ?str1) .
  BIND(STRDT(?str1, geo:wktLiteral) AS ?line) .

  BIND(?libSystem as ?layer)

  { SELECT * { ?loc wdt:P131* wd:Q99 . } }

  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
  }

