select ?WikiTree ?person ?personLabel ?statedinLabel ?refURL ?named ?cite ?page 
{

   ?person wdt:P2949 ?WikiTreeID. #personer kopplade till SKBL
#   ?person wdt:P27 wd:Q34.

  BIND (URI(CONCAT("https://www.wikitree.com/wiki/",?WikiTreeID)) AS ?WikiTree)
   ?person ?prop ?statement. #all props
   {?statement prov:wasDerivedFrom ?ref}
   OPTIONAL {?ref pr:P854 ?refURL} 
   {?ref pr:P248 ?statedin} 
   OPTIONAL {?ref pr:P1810 ?named} 
   OPTIONAL {?ref pr:P1476 ?named} 
   OPTIONAL {?ref pr:P1683 ?cite} 
   OPTIONAL {?ref pr:P304 ?page} 
   minus { ?ref pr:P248 wd:Q379406}
   {
      { ?ref pr:P248 wd:Q10478639} #Deathbook
   UNION
      { ?ref pr:P248 wd:Q10501038} #Birthbook
   UNION
     { ?ref pr:P248 wd:Q10527384} #Householdrecords
   }
   FILTER (CONTAINS(str(?refURL),'riksarkivet')) .  
   ?property wikibase:claim ?prop .
   SERVICE wikibase:label { bd:serviceParam wikibase:language "en,sv"}
}
Group by ?person ?personLabel ?statedin ?statedinLabel ?refURL ?named ?SPA ?cite ?page ?WikiTree
Order by ?personLabel ?statedinLabel

#defaultView:Map{"hide": "?rgb"}
SELECT ?d ?dLabel ?shape ?ratio ?rgb WHERE {
  ?d wdt:P31 wd:Q1149652; p:P6499 ?litpopstmt ; p:P6498 ?illitpopstmt ; wdt:P3896 ?shape.
  ?litpopstmt ps:P6499 ?litpop ; pq:P585 "+2011-01-01T00:00:00Z"^^xsd:dateTime.
  ?illitpopstmt ps:P6498 ?illitpop ; pq:P585 "+2011-01-01T00:00:00Z"^^xsd:dateTime.
  BIND(xsd:integer(?illitpop)/xsd:integer(?litpop) AS ?ratio)
  BIND(IF(?ratio < 0.2, "00008b", IF(?ratio < 0.4, "006400", IF(?ratio < 0.6, "008000", IF(?ratio < 0.8, "32cd32", IF(?ratio < 1, "7fff00", IF(?ratio < 1.2, "ffa500", IF(?ratio < 1.4, "ff6347", "ff0000"))))))) AS ?rgb)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}

