#defaultView:Map{"hide":["?coordinates", "?line", "?rgb"]}
SELECT DISTINCT ?cds ?line ?item ?itemLabel ?layer
WITH {
SELECT ?item ?cds ?lat1 ?lon1 ?layer WHERE {
?item wdt:P17 wd:Q668; wdt:P625 ?cds ; wdt:P190 [].
?item p:P625/psv:P625/wikibase:geoLatitude ?lat1 .
?item p:P625/psv:P625/wikibase:geoLongitude ?lon1 .
  ?item rdfs:label ?nl. FILTER(LANG(?nl)="en")
  BIND(?nl as ?layer)
}
} AS %stations
WITH {
SELECT ?line ?layer WHERE {
INCLUDE %stations .
  ?item wdt:P190 ?ns.
  ?item rdfs:label ?nl. FILTER(LANG(?nl)="en")
?ns p:P625/psv:P625/wikibase:geoLatitude ?lat2 .
?ns p:P625/psv:P625/wikibase:geoLongitude ?lon2 .
BIND(CONCAT('LINESTRING(', STR(?lon1), ' ', STR(?lat1), ',', STR(?lon2), ' ', STR(?lat2), ')') AS ?str) .
BIND(STRDT(?str, geo:wktLiteral) AS ?line) 
BIND(?nl as ?layer)
}
} AS %lines
WHERE {
{ INCLUDE %stations } UNION { INCLUDE %lines  } .
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} #

