SELECT ?anc ?dob ?dod ?ancLabel WITH {SELECT ?ancestor {
  {SELECT ?ancestor {wd:Q207 (wdt:P22|wdt:P25)* ?ancestor.}}
  {SELECT ?ancestor {wd:Q9682 (wdt:P22|wdt:P25)* ?ancestor.}}
}} AS %an WITH {SELECT ?ap {
  INCLUDE %an
  ?ancestor wdt:P22|wdt:P25 ?ap
}} AS %an2 WHERE {
  {SELECT DISTINCT (?ancestor AS ?anc) {INCLUDE %an}} MINUS {SELECT DISTINCT (?ap AS ?anc) {INCLUDE %an2}}
  OPTIONAL {?anc wdt:P569 ?dob.}
  OPTIONAL {?anc wdt:P570 ?dod.}
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} ORDER BY DESC(?dob)

