# People who were born over a year after one of their parents died
# Many of these indicate erroneous data-matching and may need to be fixed by hand

SELECT ?item ?itemLabel ?born ?parent ?parentLabel ?parentdied ?days_posthumous WITH {
  SELECT DISTINCT ?item ?born ?parent ?parentdied ?days_posthumous WHERE {
    ?item wdt:P31 wd:Q5 . # people only, avoid racehorses!
    # add an additional generator here if desired, ie "Swedish people" # ?item wdt:P27 wd:Q34
    ?item wdt:P25|wdt:P22 ?parent . ?item wdt:P569 ?born .
    ?parent wdt:P570 ?parentdied . filter(?parentdied < ?born) .
    bind(round((?born-?parentdied)) as ?days_posthumous) . filter(?days_posthumous > 365) .
    # filter to just those that are over a year different
    # remember that some people can be born the year after their father died!
  }
} AS %results WHERE {
  INCLUDE %results.
  ?item p:P569 [ a wikibase:BestRank; psv:P569 [ wikibase:timeValue ?born; wikibase:timePrecision ?prec1 ] ] .
  ?parent p:P570 [ a wikibase:BestRank; psv:P570 [ wikibase:timeValue ?parentdied; wikibase:timePrecision ?prec2 ] ] .
  filter (?prec1 >= 9) . filter (?prec2 >= 9)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} order by desc(?born)

SELECT DISTINCT ?item ?itemLabel ?born ?father ?fatherLabel ?fatherdied ?days_posthumous WHERE {
 ?item p:P39 ?statement. ?statement ps:P39 ?position . ?position wdt:P279* wd:Q16707842 .
 ?item wdt:P569 ?born . 
 ?item wdt:P22 ?father . ?father wdt:P570 ?fatherdied . filter(?fatherdied < ?born) .
 bind(round((?born-?fatherdied)) as ?days_posthumous) .  
 filter(?days_posthumous < 1000) # omit one massively wrong 
 SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} order by ?born

