SELECT DISTINCT ?item ?itemLabel ?line ?coords ?length WHERE {
  {
    ?item wdt:P31 wd:Q80830021.
    OPTIONAL {
      ?item p:P625 ?coords_stmt_west.
      ?coords_stmt_west pq:P518 wd:Q80981719;
        ps:P625 ?coords_west;
        (psv:P625/wikibase:geoLatitude) ?lat1;
        (psv:P625/wikibase:geoLongitude) ?lon1.
    }
    OPTIONAL {
      ?item p:P625 ?coords_stmt_east.
      ?coords_stmt_east pq:P518 wd:Q80982256;
        ps:P625 ?coords_east; 
        (psv:P625/wikibase:geoLatitude) ?lat2;
        (psv:P625/wikibase:geoLongitude) ?lon2.
    }
    OPTIONAL { ?item wdt:P2043 ?length. }
    BIND(CONCAT("LINESTRING (", STR(?lon1), " ", STR(?lat1), ",", STR(?lon2), " ", STR(?lat2), ")") AS ?str)
    BIND(STRDT(?str, geo:wktLiteral) AS ?line)
  }
  UNION
  {
    ?item wdt:P31 wd:Q80962689; 
      wdt:P625 ?coords.
  }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} 
ORDER BY (?coords_west)

