# longest rivers that feed into another river
SELECT ?river ?riverLabel (?lengthInM/1000 AS ?lengthInKm) ?mouthLabel WITH {
  SELECT DISTINCT ?river ?lengthInM ?mouth WHERE {
    ?river wdt:P31/wdt:P279* wd:Q4022;
           wdt:P403 ?mouth;
           p:P2043 ?lengthStatement. ?lengthStatement a wikibase:BestRank; psn:P2043/wikibase:quantityAmount ?lengthInM. hint:Prior hint:rangeSafe true.
    ?mouth wdt:P31/wdt:P279* wd:Q4022.
    FILTER(?lengthInM > 1000000)
  }
  ORDER BY DESC(?lengthInM)
  LIMIT 10
} AS %results WHERE {
  INCLUDE %results.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?lengthInM)

# longest rivers that don’t feed into a sea or ocean
SELECT ?river ?riverLabel (?lengthInM/1000 AS ?lengthInKm) ?mouthLabel WITH {
  SELECT DISTINCT ?river ?lengthInM ?mouth WHERE {
    ?river wdt:P31/wdt:P279* wd:Q4022;
           wdt:P403 ?mouth;
           p:P2043 ?lengthStatement. ?lengthStatement a wikibase:BestRank; psn:P2043/wikibase:quantityAmount ?lengthInM. hint:Prior hint:rangeSafe true.
    MINUS { ?mouth wdt:P31/wdt:P279* wd:Q165. }
    MINUS { ?mouth wdt:P31/wdt:P279* wd:Q9430. }
    FILTER(?lengthInM > 1000000)
  }
  ORDER BY DESC(?lengthInM)
  LIMIT 10
} AS %results WHERE {
  INCLUDE %results.
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?lengthInM)

select
  ?quantity
  (sample(?label) as ?label)
  (sample(?desc) as ?desc)
  (sample(?symbol) as ?symbol)
  ?formula
  (
    strdt(
      concat(
        "<math><mtable columnalign=left>",
        group_concat(?symbolAndLabel),
        "</mtable></math>"
      ),
      <http://www.w3.org/1998/Math/MathML>
    ) as ?otherSymbols
  )
where {
  ?quantity wdt:P279+ wd:Q71550118 .
  optional { ?quantity rdfs:label ?label . filter (lang(?label) = "en") }
  optional { ?quantity schema:description ?desc . filter (lang(?desc) = "en") }
  ?quantity wdt:P2534 ?formula .
  ?quantity wdt:P7235 ?symbol .
  {
    select distinct * where {
      ?quantity p:P4934 [ pq:P7235 ?otherSymbol ; ps:P4934 / rdfs:label ?otherLabel ] .
      filter (lang(?otherLabel) = "en")
      bind (
        concat(
          "<mrow>",
          str(?otherSymbol), "<mtext>", ": ", ?otherLabel, "</mtext>",
          "</mrow>"
        ) as ?symbolAndLabel
      )
    }
  }
}
group by ?quantity ?formula
limit 500

#defaultView:TreeMap
SELECT ?iaLabel ?a ?ab ?abLabel WITH {

SELECT ?a ?ia WHERE {
  ?ia wdt:P31 wd:Q21199 . # natural number
  ?ia wdt:P1181 ?a .      # its numeric value
  FILTER (?ia != wd:Q713113 ) # we don't want 0.9999
  FILTER (?a >= 1 && ?a <= 12 ) # between 1 and 12
} } AS %A

WITH { SELECT ?b ?ib WHERE {
  INCLUDE %A
  BIND (?a AS ?b)
  BIND (?ia AS ?ib)
}} AS %B

WHERE {
  INCLUDE %A
  INCLUDE %B
  BIND (?a * ?b AS ?ab )
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

