SELECT
  ?year ?fellow ?fellowLabel ?fellowDescription ?image ?orcid ?twitter ?github ?homepage ?wikimedia
WHERE {
  ?fellow p:P166 ?award_statement .  
  ?award_statement ps:P166 wd:Q42092479 .
  OPTIONAL {
    ?award_statement pq:P580 ?time 
    BIND(YEAR(?time) AS ?year)
  }
  OPTIONAL { ?fellow wdt:P18 ?image }
  OPTIONAL {
    ?fellow wdt:P496 ?orcid_id
    BIND(URI(CONCAT("https://orcid.org/",?orcid_id)) AS ?orcid)
  } 
  OPTIONAL {
    ?fellow wdt:P2037 ?github_name
    BIND(URI(CONCAT("https://github.com/",?github_name)) AS ?github)
  } 
  OPTIONAL {
    ?fellow wdt:P2002 ?twitter_name
    BIND(URI(CONCAT("https://twitter.com/",?twitter_name)) AS ?twitter)            
  }
  OPTIONAL {
    ?fellow wdt:P856 ?homepage
  }
  OPTIONAL {    
    ?fellow wdt:P4174 ?wikimedia_name
    BIND(URI(CONCAT("https://www.wikidata.org/w/index.php?title=Special%3ACentralAuth&target=",?wikimedia_name)) AS ?wikimedia)            
  }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en,da,de,es,fr,jp,no,ru,sv,zh" . }
}
ORDER BY DESC(?year)

