SELECT DISTINCT
  ?conf ?confLabel
  ?startDate ?endDate
  ?url 
  ?hashtag
  (GROUP_CONCAT(DISTINCT ?envLabel;separator=", ") AS ?environments)
  (GROUP_CONCAT(DISTINCT ?orgLabel;separator=", ") AS ?organizers)
WHERE {

  # web conferences with homepage
  ?conf wdt:P31/wdt:P279* wd:Q1543677 ; 
        wdt:P856 ?url .

  OPTIONAL {   # which software is used?
    ?conf wdt:P2283 ?env .
    ?env wdt:P31/wdt:P279* wd:Q7397
  }

  OPTIONAL { ?conf wdt:P580 ?startDate }
  OPTIONAL { ?conf wdt:P582 ?endDate }
  OPTIONAL { ?conf wdt:P2572 ?hashtag }
  OPTIONAL { ?conf wdt:P664 ?org }

  SERVICE wikibase:label {
    bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" .
    ?conf rdfs:label ?confLabel .
    ?env rdfs:label ?envLabel .
    ?org rdfs:label ?orgLabel .
  }
} GROUP BY ?conf ?confLabel ?url ?startDate ?endDate ?hashtag ?organizers

