SELECT DISTINCT ?region ?regionLabel ?country ?countryLabel ?area ?population ?density ?coords
WHERE 
{
  ?region wdt:P31/wdt:P279* wd:Q10864048 ;
          wdt:P17 ?country ;
          wdt:P1082 ?population ;
          p:P2046/psv:P2046 [
            wikibase:quantityAmount ?baseArea ;
            wikibase:quantityUnit [
              p:P2370/psv:P2370 [
                wikibase:quantityAmount ?unitArea;
                wikibase:quantityUnit wd:Q25343
              ]
            ]
          ] ;
          wdt:P625 ?coords .
  FILTER NOT EXISTS { ?region wdt:P576 ?x }
  BIND(?baseArea * ?unitArea / 1000000 AS ?area) .
  FILTER ( ?area > 0 )
  FILTER ( ?population > 0 )
  BIND ((?population / ?area) AS ?density)
  FILTER ( ?density < 30 )
  ?country wdt:P30 wd:Q46 .
  FILTER NOT EXISTS { ?country wdt:P17 wd:Q3024240 }
  FILTER NOT EXISTS { ?country wdt:P576 ?x }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ?density ?countryLabel ?regionLabel

#Former US presidents/vice-presidents, current age or age at death; years alive after last time an office held
#includes incumbents with current date
#by Jura1, 2020-06-20

SELECT ?person ?personLabel (MAX(?end_or_now) as ?last_date)  (MAX(?age_end_or_now) as ?age_at_date) (?when - YEAR(?last_date)  as ?years_alive_since) ?dob ?dod ?when ?age  
{
  VALUES ?office {  wd:Q11699 wd:Q11696 }
  ?st ps:P39 ?office .
  ?person wdt:P31 wd:Q5; p:P39 ?st . 
  OPTIONAL { ?st pq:P582 ?e } 
  BIND( COALESCE(?e, NOW() ) as ?end_or_now) 
  ?person wdt:P569 ?dob .
  OPTIONAL { ?person wdt:P570 ?dod } .
  BIND( COALESCE( YEAR( ?dod) , YEAR(NOW()) ) as ?when )  
  BIND( ?when - YEAR(?dob) as ?age) 
  BIND(YEAR(?end_or_now)-YEAR(?dob) as ?age_end_or_now)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en" }
}
GROUP BY ?person ?personLabel ?dob ?dod ?age ?when
ORDER BY DESC(?age)

