SELECT DISTINCT ?item ?itemLabel ?start ?parl_start ?age ?dob ?dod WHERE {
    VALUES ?parl {wd:Q16707842 wd:Q18015642 wd:Q18018860}
    ?item p:P39 ?pos_stmt .
    ?pos_stmt ps:P39 ?pos .
    ?pos wdt:P279 ?parl .
    OPTIONAL {
      ?pos_stmt pq:P580 ?start .
    }
    OPTIONAL {
      ?pos wdt:P571 ?parl_start .
    }
    BIND(COALESCE(?start, ?parl_start) AS ?best_start) .

    ?item p:P569 ?dob_stmt .
    ?dob_stmt ps:P569 ?dob .
    FILTER(isLiteral(?dob)) .
    ?dob_stmt psv:P569/wikibase:timePrecision ?dob_precision .
    FILTER(?dob_precision > 8) .

    BIND (((?best_start - ?dob)/365.24) AS ?age) .
    OPTIONAL {?item wdt:P570 ?dod} .
    SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}  ORDER BY DESC(?age)
LIMIT 200

