SELECT ?country ?nameLabel ?shape ?layer WITH {

  SELECT ?item ?name ?country where {
    values ?name {
    wd:Q14936808
    wd:Q1183757
    wd:Q1440465
    wd:Q7648632
    wd:Q7648707
    wd:Q13138161
    wd:Q14936073
    wd:Q14936243
    wd:Q14936686
    wd:Q14936925
    wd:Q14937153
    wd:Q16281769
    wd:Q18061334
    wd:Q18067250
    wd:Q18067716
    wd:Q18067726
    wd:Q18067736
    wd:Q18067785
    wd:Q18067790
    wd:Q18199125
    wd:Q19368856
    wd:Q62595789
    wd:Q78165638
    wd:Q94368759
    wd:Q97120482
    } .
    ?item wdt:P735 ?name .
    ?item wdt:P19/wdt:P17 ?country .

  } LIMIT 40000
} AS %data WITH {
  SELECT ?country ?name (COUNT(?item) AS ?pocet) {
    INCLUDE %data .
  } GROUP BY ?country ?name
} AS %counts WITH {
  SELECT ?country (MAX(?pocet) AS ?max) {
    INCLUDE %counts .
  } GROUP BY ?country
} AS %best WHERE {
  INCLUDE %counts .
  INCLUDE %best .
  FILTER( ?max = ?pocet ) .
  ?name rdfs:label ?nameLabel filter(lang(?nameLabel)="en").
  ?country wdt:P3896 ?shape .
  BIND((?nameLabel) as ?layer ). 
}

