{
  ?term wdt:P279 wd:Q16707842 .
  ?person p:P39 ?ps1 . ?ps1 ps:P39 ?term . ?ps1 pq:P580 ?s1 . ?ps1 pq:P582 ?resigned . 
  ?ps1 pq:P768 ?old_seat . ?ps1 pq:P1534 wd:Q796919 .

  ?person p:P39 ?ps2 . ?ps2 ps:P39 ?term . ?ps2 pq:P580 ?elected . ?ps2 pq:P768 ?new_seat .

  filter (?old_seat != ?new_seat) . 
  filter (?elected > ?resigned) . 
  bind(round(?elected - ?resigned) as ?diff) .

  filter not exists { 
    ?person p:P39 ?ps3 . ?ps3 ps:P39 ?term . ?ps3 pq:P580 ?s3 . ?ps3 pq:P582 ?e3 .
    filter (?s3 > ?resigned ) . filter (?elected > ?e3 ) }  
