# Italian politicians who served as a minister in more than one cabinet since 1990
# Query by Andrea Mignone (https://twitter.com/i_m_andrea)
#defaultView:Graph
SELECT distinct ?person ?personLabel ?gov ?govLabel ?rgb ?size
WHERE {

  ?person p:P39 ?statement.
  ?person p:P39 ?statement2.

  ?statement pq:P5054 ?gov;
             pq:P580 ?date;
             ps:P39 ?edge.

  ?statement2 pq:P5054 ?gov2;
              pq:P580 ?date2;
              ps:P39 ?edge2.

  ?edge wdt:P31 | wdt:P279 wd:Q3858501.
  ?edge2 wdt:P31 | wdt:P279 wd:Q3858501.

  FILTER(YEAR(?date) >= 1990)
  FILTER(YEAR(?date2) >= 1990)
  FILTER(?gov != ?gov2)

  # cosmetic stuff
  BIND('8F2539' as ?rgb)
  BIND(2000 as ?size)

  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

