#Vito Russo Test result for each film along with the score, if in Wikidata:
#0/3: no identifiably LGBTQIA+ characters
#1/3: at least one character that is identifiably LGBTQIA+
#2/3: at least one character that is identifiably LGBTQIA+ (not predominantly defined by their orientation/identity)
#passing: at least one character that is identifiably LGBTQIA+ (not predominantly defined by their orientation/identity)
#whose removal would have a significant effect on the plot
SELECT ?item ?itemLabel (MIN(YEAR(?date)) AS ?year) (GROUP_CONCAT(DISTINCT ?country_label; separator = ", ") AS ?countries) ?VRTresult ?score ?nature_of_statement WHERE {
{?item p:P1552 ?st. ?st ps:P1552 wd:Q93615557. BIND("passing" AS ?VRTresult)}
UNION
{?item p:P1552 ?st. ?st ps:P1552 wd:Q93985027. BIND("failing" AS ?VRTresult)}
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
OPTIONAL{?st pq:P444 ?score}
OPTIONAL{?st pq:P5102 ?noc. ?noc rdfs:label ?nature_of_statement. FILTER(LANG(?nature_of_statement)="en")}
OPTIONAL{?item wdt:P577 ?date}
OPTIONAL{?item wdt:P495 ?country. ?country rdfs:label ?country_label. FILTER(LANG(?country_label)="en")}
SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
GROUP BY ?item ?itemLabel ?VRTresult ?score ?nature_of_statement
ORDER BY ?year

