{SELECT distinct ?mp ?mpLabel ?pLabel ?sLabel ?start
WHERE{?mp p:P39 ?ps.?ps ps:P39 ?t;pq:P768 ?s;pq:P4100 ?p;pq:P580 ?start.?t wdt:P279 wd:Q16707842. 
minus{?ps pq:P2715 ?elec.?elec wdt:P31 wd:Q15283424.?mp p:P39[ps:P39 ?t0; pq:P4100 ?p; pq:P768 ?s; pq:P1534 wd:Q741182].?t0 wdt:P156 ?t}
minus{?ps pq:P1534 wd:Q50393121} # omit untaken double-return seats
