SELECT ?item (sample(?lat1) as ?lat1) (sample(?lon1) as ?lon1) WHERE {
?item wdt:P31/wdt:P279* wd:Q55488; wdt:P625 ?cds. MINUS { ?item wdt:P31/wdt:P279* wd:Q928830 }
{?item wdt:P17 wd:Q668}
?connect wdt:P31 wd:Q15141321 ; wdt:P17 wd:Q668 ; wdt:P1427 wd:Q986105 .
?item wdt:P1192 ?connect.
?item p:P625/psv:P625/wikibase:geoLatitude ?lat1 .
?item p:P625/psv:P625/wikibase:geoLongitude ?lon1 .
} GROUP BY ?item
} AS %stations
WITH {
SELECT ?line ?connect ?ns WHERE {
INCLUDE %stations .
?item p:P1192 [ ps:P1192 ?connect ; pq:P156 ?ns ] .
?connect wdt:P31 wd:Q15141321 ; wdt:P17 wd:Q668 ; wdt:P1427 wd:Q986105 .
?ns p:P625/psv:P625/wikibase:geoLatitude ?lat2 .
?ns p:P625/psv:P625/wikibase:geoLongitude ?lon2 .
BIND(CONCAT('LINESTRING(', STR(?lon1), ' ', STR(?lat1), ',', STR(?lon2), ' ', STR(?lat2), ')') AS ?str) .
BIND(STRDT(?str, geo:wktLiteral) AS ?line) 
}
} AS %lines
WHERE {
{ INCLUDE %lines  }.
SERVICE wikibase:label { bd:serviceParam wikibase:language "bn,en". ?connect rdfs:label ?connectLabel. ?ns rdfs:label ?nsLabel}
} ORDER BY ?connectLabel

