#defaultView:Map
SELECT
  ?count ?layer
  ?organization ?organizationLabel
  ?example_author ?example_authorLabel
  ?example_work ?example_workLabel
  ?geo
WITH {
  SELECT
    (COUNT(*) AS ?count) 
    ?organization
    (SAMPLE(?author) AS ?example_author)
    (SAMPLE(?work) AS ?example_work)
  {
    ?taxon wdt:P171+ wd:Q1134583 .
    ?work wdt:P921 ?taxon ;
          wdt:P577 ?date ;
          wdt:P50 ?author .
    ?author (wdt:P108 | wdt:P1416) ?organization . 
    FILTER(YEAR(?date) < 2020)
  }
  GROUP BY ?organization
  LIMIT 100
} AS %result
WHERE {
  INCLUDE %result
  ?organization wdt:P159? / wdt:P625 ?geo
  BIND(IF(?count > 10, IF(?count >100, ">100", ">10"), "<=10") AS ?layer) 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

