SELECT distinct ?term ?termLabel ?start ?end ?members ?father_was_mp ?mother_was_mp where
{
  { SELECT distinct ?term ?start ?end where 
   { ?term wdt:P279 wd:Q16707842 . ?term wdt:P571 ?start; wdt:P576 ?end .
     filter(year(?start) >= 1900 ) } } 
 optional  { SELECT ?term (count(distinct ?item) as ?members) where 
    { ?item p:P39 [ ps:P39 ?term; pq:P768 ?const] . ?term wdt:P279 wd:Q16707842 . } group by ?term } 
 optional  { SELECT ?term (count(distinct ?item) as ?father_was_mp) where 
    { ?item p:P39/ps:P39 ?term . ?term wdt:P279 wd:Q16707842 .
      ?item wdt:P22/p:P39/ps:P39 ?term2 . 
      { ?term2 wdt:P279* wd:Q16707842 } union { ?term2 wdt:P279* wd:Q18015642 }
    } group by ?term }
 optional  { SELECT ?term (count(distinct ?item) as ?mother_was_mp) where 
    { ?item p:P39/ps:P39 ?term . ?term wdt:P279 wd:Q16707842 .
      ?item wdt:P25/p:P39/ps:P39 ?term2 . 
      { ?term2 wdt:P279* wd:Q16707842 } union { ?term2 wdt:P279* wd:Q18015642 }
    } group by ?term }
  SERVICE wikibase:label { bd:serviceParam wikibase:language 'en' }
} order by ?start

