#aksara dengan tahun kemunculannya
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
SELECT distinct ?aksara ?aksaraLabel ?tgl ?tahun (sample(?ga) as ?gam) WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "id,en". }
  ?aksara wdt:P31/wdt:P279* wd:Q8192.
  MINUS { ?aksara wdt:P31/wdt:P279* wd:Q17451.} #bukan rupa huruf
  MINUS { ?aksara wdt:P31 wd:Q53764738.}
  MINUS { ?aksara wdt:P31 wd:Q16744570.}
  MINUS { ?aksara wdt:P31 wd:Q29575627.}
  MINUS { ?aksara wdt:P31/wdt:P279* wd:Q12681.}
  OPTIONAL { ?aksara wdt:P571 ?tgl. }
  OPTIONAL { ?aksara wdt:P18 ?ga. }
  BIND ( year(?tgl) AS ?th ).
  BIND ( xsd:integer(?th) - IF(?th<0,IF(STRENDS(STR(?th),"9"),1,0),0) AS ?th2). #kurangi satu tahun jika tahun SM karena tampilan tahun SM default bertambah 1
  BIND ( IF(?th2<0,CONCAT(STR(?th2 * -1)," SM"),?th2) AS ?tahun).
}
GROUP BY ?aksara ?aksaraLabel ?tgl ?tahun
#defaultView:Timeline
#view:Timeline{"hide": "?tgl"}

