SELECT ?property ?propertyLabel ?uses ?example_preprint ?example_preprint_title ?example_article ?example_article_title
  WITH {
    SELECT ?directProperty (COUNT(?preprint) as ?uses) (SAMPLE(?preprint) as ?example_preprint) (SAMPLE(?article) as ?example_article) WHERE {
      ?preprint wdt:P31 wd:Q580922 .
      ?article wdt:P31 wd:Q13442814 .
      ?preprint ?directProperty ?article .
      FILTER(STRSTARTS(STR(?directProperty), STR(wdt:)))
    } GROUP BY ?directProperty
  } AS %properties
  WHERE {
    INCLUDE %properties .
    ?property wikibase:directClaim ?directProperty .
    ?example_preprint wdt:P1476 ?example_preprint_title .
    ?example_article wdt:P1476 ?example_article_title .
    SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  } ORDER BY desc(?uses)

SELECT distinct ?parliament ?parliamentLabel ?start ?end ?members ?distinct_terms 
(?distinct_terms - ?with_constituencies as ?missing_constituencies)
(?distinct_terms - ?with_parties as ?missing_parties) where
{
  { SELECT distinct ?parliament ?start ?end where 
   { ?parliament wdt:P279 wd:Q16707842 . optional { ?parliament wdt:P571 ?start } optional { ?parliament wdt:P576 ?end } } } 

 optional  { SELECT ?parliament (count(distinct ?item) as ?members) (count(distinct ?ps) as ?distinct_terms) where 
    { ?item p:P39 ?ps . ?ps ps:P39 ?parliament . ?parliament wdt:P279 wd:Q16707842 . } group by ?parliament } 

 optional { SELECT ?parliament (count(distinct ?ps) as ?with_constituencies) where 
    { ?item p:P39 ?ps . ?ps ps:P39 ?parliament . ?parliament wdt:P279 wd:Q16707842 . ?ps pq:P768 ?const } group by ?parliament } 

 optional { SELECT ?parliament (count(distinct ?ps) as ?with_parties) where 
    { ?item p:P39 ?ps . ?ps ps:P39 ?parliament . ?parliament wdt:P279 wd:Q16707842 . ?ps pq:P4100 ?party } group by ?parliament } 

  SERVICE wikibase:label { bd:serviceParam wikibase:language 'en' }
} order by ?start

