#defaultView:Map
SELECT DISTINCT ?item ?itemLabel ?length ?coords
WHERE 
{
  ?item wdt:P31 wd:Q4022;
        wdt:P403/wdt:P361* wd:Q788 ;
        p:P2043/psv:P2043 [
        wikibase:quantityAmount ?baseLength;
        wikibase:quantityUnit [
          p:P2370/psv:P2370 [
            wikibase:quantityAmount ?unitLength;
            wikibase:quantityUnit wd:Q11573
          ]
        ]
      ].
  BIND(ROUND(?baseLength * ?unitLength / 1000) AS ?length) .
  OPTIONAL {
    {
      ?item p:P625 ?coordsStatement .
      ?coordsStatement ps:P625 ?coords ;
                       pq:P518 wd:Q1233637 .
    } UNION {
      ?item p:P625 ?coordsStatement .
      ?coordsStatement ps:P625 ?coords .
      FILTER NOT EXISTS { ?coordsStatement pq:P518 ?x }
    }
  }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?length)

