#title:Economists with an enwiki articles about themselves, but not their wife ;)
# adapted from https://twitter.com/wikigamaliel/status/1440678869539844110
SELECT ?item ?itemLabel ?sls ?wife ?wifeLabel ?wifeDescription ?slswife 
WITH
{ 
  SELECT ?item  ?article
  WHERE
  {
  ?item wdt:P21 wd:Q6581097;
        wdt:P106 wd:Q188094.
  ?article schema:about ?item ; 
           schema:isPartOf <https://en.wikipedia.org/> .
  } 
} AS %i
WHERE
{
  INCLUDE %i
  ?item wdt:P26 ?wife . 
   FILTER NOT EXISTS { ?wen schema:about ?wife; schema:isPartOf <https://en.wikipedia.org/> . } 
  ?item wikibase:sitelinks ?sls .
  ?wife wikibase:sitelinks ?slswife .
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

