SELECT ?member ?memberLabel ?descendants ?descendants_were_MPs
WITH { 
  select distinct ?member WHERE 
     { ?member wdt:P31 wd:Q5 . ?member p:P39 ?pss . ?pss ps:P39 ?st . ?st wdt:P279 wd:Q16707842 .
     } } AS %members 
WHERE {
 { SELECT distinct ?member (count(distinct ?child) as ?descendants) where 
    { INCLUDE %members . ?member wdt:P40+ ?child } group by ?member } 
 optional { SELECT distinct ?member (count(distinct ?MPchild) as ?descendants_were_MPs) where 
    { INCLUDE %members . ?member wdt:P40+ ?MPchild .
      ?MPchild p:P39 ?psc . ?psc ps:P39 ?ct . ?ct wdt:P279 wd:Q16707842 .
    } group by ?member } 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} order by desc(?descendants_were_MPs) desc(?children)

