#Chelsea FC players
#defaultView:ImageGrid
SELECT DISTINCT ?playerItem ?player ?startDate ?endDate ?position ?games ?goals ?image ?article ?rank

WHERE
{
  ?playerItem p:P54 ?teamStat .        
  ?playerItem wdt:P18 ?image .
  ?playerItem wikibase:sitelinks ?rank .
  OPTIONAL {?playerItem wdt:P413 ?positionItem}

  ?teamStat ps:P54 wd:Q9616 .
  OPTIONAL {?teamStat pq:P1350 ?games}
  OPTIONAL {?teamStat pq:P1351 ?goals}
  #FILTER (?games > 0)

  ?teamStat pqv:P580 ?startStatement .
  ?startStatement wikibase:timeValue ?startDate .
