  }
}
ORDER BY DESC(?rank)

SELECT ?award ?awardLabel (SUM(?female) AS ?count_females) (SUM(?male) AS ?count_males) (COUNT(*) AS ?count) ((100 * ?count_females / ?count) AS ?share_female) WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  ?award wdt:P279 wd:Q7191.
  ?item wdt:P166 ?award;
    wdt:P21 ?gender.
  BIND(IF(?gender IN(wd:Q6581097, wd:Q2449503), 1 , 0 ) AS ?male)
  BIND(IF(?gender IN(wd:Q6581072, wd:Q1052281), 1 , 0 ) AS ?female)
}
GROUP BY ?award ?awardLabel
ORDER BY DESC(?share_female)

# tool: scholia
#defaultView:ScatterChart
# Statistics on number of recipients for award wrt. gender
SELECT ?men ?women ?award ?awardLabel 
WITH {
  SELECT (COUNT(?recipient_male) AS ?men) (COUNT(?recipient_female) AS ?women) ?award WHERE {
    ?award wdt:P31 wd:Q11448906 .
    { 
      ?recipient_male wdt:P166 ?award .
      ?recipient_male wdt:P21 wd:Q6581097 .
    }
    UNION 
    {
      ?recipient_female wdt:P166 ?award .
      ?recipient_female wdt:P21 wd:Q6581072 .
    }  
  }
  GROUP BY ?award
} AS %result
WHERE {
  INCLUDE %result
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en,da,de,es,fr,jp,nl,no,ru,sv,zh". }
} 
ORDER BY DESC(?men) DESC(?women)

# query made User:PAC2
#title: Share of women among Nobel prize winners by decade
SELECT ?decade (SUM(?female) AS ?count_females) (SUM(?male) AS ?count_males) (COUNT(*) AS ?count) ((100  * ?count_females) / ?count AS ?share_female) WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  ?award wdt:P279 wd:Q7191.
  ?item p:P166 _:b1;
    wdt:P21 ?gender.
  _:b1 ps:P166 ?award;
    pq:P585 ?date.
  BIND(CONCAT(STR(ROUND((YEAR(?date)) / 10 )), "0") AS ?decade)
  BIND(IF(?gender IN(wd:Q6581097, wd:Q2449503), 1 , 0 ) AS ?male)
  BIND(IF(?gender IN(wd:Q6581072, wd:Q1052281), 1 , 0 ) AS ?female)
}
GROUP BY ?decade
ORDER BY DESC (?decade)

