# UK railway stations connected from Brussels-South railway station (Q800587) 
#defaultView:Map{"hide":["?cds","?line","?layer"],"layer":"?trackLabel"}
SELECT ?station ?stationLabel ?cds ?line ?trackLabel ?track ?image {
{SELECT * { SERVICE gas:service { gas:program gas:gasClass "com.bigdata.rdf.graph.analytics.BFS" ; gas:in wd:Q800587 ; gas:linkType wdt:P197 ; gas:out ?station ; gas:out1 ?depth ; gas:out2 ?pred . } FILTER(?station != wd:Q800587)
  ?station wdt:P625 ?cds ; wdt:P17 ?country .
  VALUES ?country { wd:Q31 wd:Q32 wd:Q55 }#Be, Lu, NL
  ?station p:P197 ?adjST . ?adjST ps:P197 ?adj .
  OPTIONAL { ?adjST pq:P81 ?track .}
  MINUS { ?station p:P31 [ ps:P31 wd:Q55488 ; pq:P582 [] ] . } #connections that are no longer 
  MINUS { ?adj p:P31 [ ps:P31 wd:Q55488 ; pq:P582 [] ] . }
  ?adj p:P625/psv:P625/wikibase:geoLatitude ?lat1 ; p:P625/psv:P625/wikibase:geoLongitude ?lon1 .
  ?station p:P625/psv:P625/wikibase:geoLatitude ?lat2 ; p:P625/psv:P625/wikibase:geoLongitude ?lon2 .
  BIND(CONCAT('LINESTRING(', STR(?lon1), ' ', STR(?lat1), ',', STR(?lon2), ' ', STR(?lat2), ')') AS ?str) . BIND(STRDT(?str, geo:wktLiteral) AS ?line)}
}
  OPTIONAL { ?station wdt:P18 ?image .}
SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}

SELECT ?station ?stationLabel ?coordinate_location ?start ?end WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "da,en". }
  ?station (wdt:P31/(wdt:P279*)) wd:Q55488;
    wdt:P17 wd:Q35;
    wdt:P625 ?coordinate_location.
  FILTER((BOUND(?start)) || (BOUND(?end)))
  OPTIONAL { ?station wdt:P1619 ?date_of_official_opening. }
  OPTIONAL { ?station wdt:P571 ?inception. }
  OPTIONAL { ?station wdt:P3999 ?closure. }
  OPTIONAL { ?station wdt:P582 ?endtime. }
  BIND(IF(BOUND(?date_of_official_opening), ?date_of_official_opening, ?inception) AS ?start)
  BIND(IF(BOUND(?closure), ?closure, ?endtime) AS ?end)
}

