#defaultView:Map
SELECT DISTINCT ?author ?authorLabel ?organisation ?organisationLabel (COALESCE(?orcid_, CONCAT("orcid-search/quick-search/?searchQuery=", ?authorLabel)) AS ?orcid) 
                ?exampleWork ?exampleWorkLabel (SAMPLE(?location) AS ?where)
WITH {
  SELECT (SAMPLE(?work) AS ?exampleWork) ?author WHERE {
    { ?work wdt:P921/wdt:P31*/wdt:P279* wd:Q2013 . }
    union { ?work wdt:P921/wdt:P361+ wd:Q2013 . }
    union { ?work wdt:P921/wdt:P1269+ wd:Q2013 . }
    ?work wdt:P50 ?author .
  } GROUP BY ?author
} AS %result
WHERE {
  INCLUDE %result
  VALUES ?COUNTRY { wd:Q155 wd:Q414 wd:Q298 wd:Q419 wd:Q739 wd:Q750 wd:Q717 wd:Q733 wd:Q736 wd:Q734 wd:Q77 wd:Q730 }
  ?author wdt:P108 | wdt:P1416 ?organisation .
  { ?organisation wdt:P131 ?COUNTRY ; wdt:P625 ?location . }
  UNION
  { ?organisation wdt:P159 ?hq . ?hq wdt:P17 ?COUNTRY . ?hq wdt:P625 ?location . }
  UNION
  { ?organisation wdt:P17 ?COUNTRY ; wdt:P625 ?location . }
  # Include optional ORCID iD
  OPTIONAL { ?author wdt:P496 ?orcid_ . }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en,da,de,es,fr,jp,nl,no,ru,sv,zh" . } 
} GROUP BY ?author ?authorLabel ?organisation ?organisationLabel ?orcid_ ?exampleWork ?exampleWorkLabel
ORDER BY DESC(?authorLabel)

# (Thanks, James Heald, for the original query!)
#defaultView:Map
SELECT ?station ?stationLabel ?cds ?line ?layer WHERE { 
  { SERVICE gas:service {
       gas:program gas:gasClass "com.bigdata.rdf.graph.analytics.BFS" ; gas:in wd:Q3097173 ; gas:target wd:Q6132101 ; gas:out ?station ; gas:out1 [] ; gas:out2 ?pred .
       gas:program gas:linkType wdt:P197 . } }
  FILTER(?station != wd:Q3097173)
  ?pred p:P625/psv:P625/wikibase:geoLatitude ?lat1 ; p:P625/psv:P625/wikibase:geoLongitude ?lon1 .
  ?station p:P625/psv:P625/wikibase:geoLatitude ?lat2 ; p:P625/psv:P625/wikibase:geoLongitude ?lon2 .
  BIND(CONCAT('LINESTRING(', STR(?lon1), ' ', STR(?lat1), ',', STR(?lon2), ' ', STR(?lat2), ')') AS ?str) . BIND(STRDT(?str, geo:wktLiteral) AS ?line) 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en,th". }
}

