#title:Number of virtual twins (pair with the same dates of birth and death) in Wikidata at a given milestone, from [[d:Wikidata:WikiProject Virtual Twins]]
#defaultView:AreaChart
SELECT ?date ?cumulativecount 
{
    hint:Query hint:optimizer "None".
    {   SELECT ?milestonep (COUNT(?item) as ?cumulativecount)
        WHERE
        {
          { SELECT ?item { {
  	?item p:P1889 [ pq:P1013 wd:Q20978290 ; ps:P1889 ?item2 ]
    FILTER( xsd:integer(strafter(str(?item),"Q")) >  xsd:integer(strafter(str(?item2),"Q"))  )         
  } }   }
          BIND( xsd:integer( substr(str(?item), 33)) as ?qid)
          wd:Q38074555 p:P1114 ?milestonep .
          ?milestonep ps:P1114 ?milestone .
          FILTER( ?milestone > ?qid ) 
        }
        GROUP BY ?milestonep
    }          
    ?milestonep pq:P585 ?date
}

#title:People recently buried in a cemetery nearby without an image of the grave (P1442) on Wikidata
#defaultView:Map{"hide":["?coor"]}
#by Jura1, 2021-11-0, source: https://www.wikidata.org/wiki/Property_talk:P1442#Queries
SELECT
  ?item ?itemLabel ?itemDescription 
  ?date_of_death 
  ?cemetery ?cemeteryLabel ?coor 
  ?commons_category_which_may_include_one ?findagrave 
  ?img_person ?img_cemetery
{
  ?item wdt:P119 ?cemetery.
  ?cemetery wdt:P31 wd:Q39614.
  ?item wdt:P570 ?date_of_death.
  FILTER (?date_of_death >"2015-01-01"^^xsd:dateTime)
  FILTER NOT EXISTS {?item p:P1442 []}
  ?cemetery wdt:P625 ?coor.
  FILTER NOT EXISTS {?cemetery wdt:P625 ?c2.FILTER(str(?coor)>str(?c2))}
  OPTIONAL{?item wdt:P373 ?cat.BIND(URI(CONCAT("https://commons.wikimedia.org/wiki/Category:",?cat)) as ?commons_category_which_may_include_one)}
  OPTIONAL{?item wdt:P535 ?fid.wd:P535 wdt:P1630 ?ff.BIND(URI(REPLACE(?ff,"\\$1",?fid)) as ?findagrave)}
  OPTIONAL{?item wdt:P18 ?img_person}
  FILTER NOT EXISTS {?item wdt:P18 ?i2.FILTER(str(?i2)>str(?img_person))}
  OPTIONAL{?cemetery wdt:P18 ?img_cemetery}
  FILTER NOT EXISTS {?cemetery wdt:P18 ?i3.FILTER(str(?i3)>str(?img_cemetery))}
  SERVICE wikibase:label{ bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en,fr"}
}

