SELECT DISTINCT ?item ?itemLabel ?countryLabel ?area ?population ?density ?coords
WHERE 
{
  ?item p:P31 ?statement ;
        wdt:P17 ?country ;
        wdt:P625 ?coords .
  ?statement ps:P31/wdt:P279* wd:Q10864048 .
  FILTER NOT EXISTS { ?statement pq:P582 ?x }
  FILTER NOT EXISTS { ?country wdt:P31 wd:Q3024240 }
  FILTER NOT EXISTS { ?item wdt:P576 ?x }

  ?item p:P2046 ?areaStatement .
  { FILTER NOT EXISTS { ?areaStatement pq:P518 ?x } } #UNION
  #{ ?areaStatement pq:P518 wd:Q16868672 }
  ?areaStatement psv:P2046 [
        wikibase:quantityAmount ?baseArea;
        wikibase:quantityUnit [
          p:P2370/psv:P2370 [
            wikibase:quantityAmount ?unitArea;
            wikibase:quantityUnit wd:Q25343
          ]
        ]
      ].
  BIND(ROUND(?baseArea * ?unitArea / 1000000) AS ?area) .
  FILTER (?area >= 100000)

  ?item wdt:P1082 ?population .
  BIND(ROUND(100 * ?population / ?area) / 100 AS ?density)

  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?area)

