SELECT
  ?number_of_citing_works
  ?author ?authorLabel
  ?organization ?organizationLabel
  ?example_work ?example_workLabel
WITH {
  SELECT DISTINCT ?author WHERE {
    ?author wdt:P27| wdt:P1416/wdt:P17 | wdt:P108/wdt:P17 wd:Q117 .
  }
} AS %authors
