# Most common classes of the values of "depicts" (P180) on Commons
SELECT ((?sum / 40) AS ?percent) ?class ?class_label ?instance ?instance_label ?sample

WITH { 
  SELECT (COUNT(DISTINCT(?file)) AS ?count) ?value (SAMPLE(?file) AS ?example) WHERE {
     service bd:sample {
       ?file wdt:P180 ?value . 
        bd:serviceParam bd:sample.limit 4000 .
        bd:serviceParam bd:sample.sampleType "RANDOM" .
     }  
  } GROUP BY ?value
  ORDER BY DESC(?count)
#  LIMIT 2000           
} AS %values

WITH {
  SELECT (SUM(?count) AS ?sum) ?class (SAMPLE(?value) AS ?instance) (SAMPLE(?example) AS ?sample) WHERE {
    INCLUDE %values .
    service <https://query.wikidata.org/sparql> {
         ?value wdt:P31 ?class
    }
  } GROUP BY ?class 
} AS %classes

WHERE {
  INCLUDE %classes . 
  service <https://query.wikidata.org/sparql> {
      OPTIONAL {?instance rdfs:label ?instance_label FILTER (lang(?instance_label) = 'en') } .
      OPTIONAL {?class rdfs:label ?class_label FILTER (lang(?class_label) = 'en') } .
  }
} 
ORDER BY DESC(?sum)

#Drainage basins of Scottish rivers 
#defaultView:Map
SELECT DISTINCT ?layer ?layerLabel ?dot ?dotLabel ?coord 

WITH {
  SELECT DISTINCT ?somethingelse ?sea WHERE {
    {?somethingelse wdt:P131/wdt:P131 wd:Q22 .} hint:Prior hint:runFirst true.
    ?somethingelse wdt:P403|wdt:P201 ?sea . 
  } 
} as %i

WITH { 
  SELECT ?sea ?layer WHERE {
    INCLUDE %i .
    FILTER NOT EXISTS {
      ?sea wdt:P31/wdt:P279* wd:Q355304 . hint:Prior hint:gearing "forward". # exclude watercourse
      FILTER NOT EXISTS {?sea wdt:P31 wd:Q47053. }   # unless estuary,
      FILTER NOT EXISTS {?sea wdt:P31 wd:Q1210950. } # or channel
    }
    FILTER NOT EXISTS {?sea wdt:P403?/wdt:P31/wdt:P279* wd:Q23397 . hint:Prior hint:gearing "forward". } # lake
    ?sea ^wdt:P403|^wdt:P201 ?layer. # ?layer is the river before the sea
    ?layer wdt:P131/wdt:P131 wd:Q22 . hint:Prior hint:gearing "forward".
  } 
} as %j

WHERE {
  INCLUDE %j
  {?layer (^wdt:P403|^wdt:P201|wdt:P200)*/(^wdt:P177|^wdt:P206)? ?dot. }
  ?dot wdt:P625|((p:P885|p:P403|p:P200|p:P201)/pq:P625) ?coord .

  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}

