#defaultView:Map{"hide": ["?shape", "?coords"]}
SELECT  ?item ?countryLabel ?itemLabel ?population ?coords ?shape ?layer
WHERE 
{
  ?item wdt:P31/wdt:P279* wd:Q10864048 ;
        wdt:P1082 ?population ;
        wdt:P17 ?country .
  FILTER ( ?population > 10000000 )
  FILTER NOT EXISTS { ?item wdt:P31 wd:Q19953632 }
  FILTER NOT EXISTS { ?item wdt:P576 ?x }
  OPTIONAL { ?item wdt:P625 ?coords }
  OPTIONAL { ?item wdt:P3896 ?shape }
  BIND(
    IF(?population < 20000000, "10M-20M",
    IF(?population < 30000000, "20M-30M",
    IF(?population < 40000000, "30M-40M",
    IF(?population < 50000000, "40M-50M",
    IF(?population < 60000000, "50M-60M",
    IF(?population < 70000000, "60M-70M",
    IF(?population < 80000000, "70M-80M",
    IF(?population < 90000000, "80M-90M",
    IF(?population < 100000000, "90M-100M",   
    "100M+")))))))))
    AS ?layer)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY ?population

#title: List of globes and how many times they've been used
select *
with {
  select ?globe (count(*) as ?total) {
      [] wikibase:geoGlobe ?globe.
    } group by ?globe
} as %globes
{
  include %globes.
  optional { ?globe rdfs:label ?en filter (lang(?en) = "en") }
} order by desc(?total) ?en

