}

#title:scholarly papers by University of Leeds researchers in White Rose repository with a free licence
SELECT DISTINCT ?paper ?paperLabel ?licenceLabel ?fulltext WHERE {
  ?author wdt:P108 wd:Q503424.   # Employee of University of Leeds
  ?paper wdt:P31 wd:Q13442814; wdt:P50 ?author.   # scholarly article by this author
  VALUES ?freelicence {wd:Q6905323 wd:Q6905942}   # CC-BY or CC-BY-SA
  ?paper wdt:P275 ?licence . ?licence wdt:P629? ?freelicence.  # Article has this licence, or a specific edition such as CC-BY 3.0
  ?paper wdt:P953 ?fulltext FILTER(CONTAINS(STR(?fulltext), "whiterose.ac.uk"))
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en"}
} ORDER BY ?paper

#defaultView:Map
SELECT
  ?coordinates
  ?institution
  ?institutionLabel
  ?award
  ?awardLabel
  ?item
  ?itemLabel
WITH {
  SELECT DISTINCT ?item {
    ?item wdt:P31 wd:Q5;
          wdt:P103 wd:Q188.
  }
} AS %0
WITH {
  SELECT DISTINCT ?item ?award {
    INCLUDE %0.
    ?item wdt:P166 ?award.
  }
} AS %1
WITH {
  SELECT DISTINCT ?institution ?award {
    INCLUDE %1.
    {?institution wdt:P5460 ?award}
    UNION
    {?award wdt:P1027 ?institution}.
  }
} AS %2
WITH {
  SELECT DISTINCT ?institution ?coordinates {
    INCLUDE %2.

    OPTIONAL{?institution wdt:P625 ?P625}.
    OPTIONAL{?institution wdt:P159/wdt:P625 ?P159_P625}.
    OPTIONAL{?institution wdt:P131+/wdt:P625 ?P131_P625}.

    BIND(COALESCE(IF(BOUND(?P625), ?P625, 1/0),
                  IF(BOUND(?P159_P625), ?P159_P625, 1/0),
                  IF(BOUND(?P131_P625), ?P131_P625, 1/0))
         AS ?coordinates)

    FILTER(BOUND(?coordinates))
  }
} AS %3
{
  INCLUDE %1.
  INCLUDE %2.
  INCLUDE %3.
  SERVICE wikibase:label {bd:serviceParam wikibase:language "en"}.
}

