#title:Most recent date not used as a date of birth (P569) or date of death (P570)
SELECT ?date

WITH {
  SELECT ?date WHERE {
    wd:Q47150325 ^wdt:P31 [wdt:P585 ?date] .
    FILTER(xsd:date(?date) < xsd:date(NOW()))
  }
} AS %results1 

WITH {
  SELECT ?date WHERE {
    INCLUDE %results1.
    MINUS{[] ps:P570 ?date .}
  }
} AS %results2

WHERE {
  INCLUDE %results2.
  MINUS{[] ps:P569 ?date .}
} ORDER BY DESC(?date) LIMIT 1

