#title: Longest rivers in France
SELECT DISTINCT ?item ?itemLabel ?length ?unit ?unitLabel ?length_in_km WHERE {
  ?item (wdt:P31/(wdt:P279*)) wd:Q55659167;
    wdt:P17 wd:Q142;
    p:P2043 ?length_statement.
  ?length_statement psv:P2043 ?length_value.
  ?length_value wikibase:quantityAmount ?length;
    wikibase:quantityUnit ?unit.
  ?unit p:P2370 ?unit_statement.
  ?unit_statement psv:P2370 ?unit_value.
  ?unit_value wikibase:quantityAmount ?conversion;
    wikibase:quantityUnit wd:Q11573.
  BIND(?length * ?conversion / 1000 AS ?length_in_km)
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE], en". }
}
ORDER BY DESC (?length_in_km)
LIMIT 100

select distinct ?form1 ?form1Label ?form2 ?form2Label {
  ?item ontolex:lexicalForm ?form1, ?form2;
        dct:language wd:Q9027;
        wikibase:lexicalCategory wd:Q24905.
  filter(str(?form1) < str(?form2)).
  minus { ?form1 ?thing [] filter (strstarts(str(?thing), str(p:))) }
  minus { ?form2 ?thing [] filter (strstarts(str(?thing), str(p:))) }
  ?form1 ontolex:representation ?form1Label.
  ?form2 ontolex:representation ?form2Label.
  minus { ?item dct:language wd:Q9027; ontolex:lexicalForm ?form1. ?form1 wikibase:grammaticalFeature ?feat. filter not exists { ?form2 wikibase:grammaticalFeature ?feat } }
  minus { ?item dct:language wd:Q9027; ontolex:lexicalForm ?form2. ?form2 wikibase:grammaticalFeature ?feat. filter not exists { ?form1 wikibase:grammaticalFeature ?feat } }
}

