SELECT ?base ?count ?sample_item ?sample_itemLabel ?sample_itemDescription ?sample_url WHERE {
  {
    select (IRI(CONCAT(?stem, '.uk/')) AS ?base) (count(?item) as ?count) (SAMPLE(?item) AS ?sample_item) (SAMPLE(?url) AS ?sample_url) {
      ?item wdt:P973 ?url .
      FILTER(CONTAINS(str(?url), '.uk/')) .
      BIND(STRBEFORE(str(?url), '.uk/') AS ?stem) .                
    } group by ?stem
  }
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
}
ORDER BY DESC(?count)

