SELECT DISTINCT ?url ?code ?named_as ?links ?stmts ?item ?itemLabel ?itemDescription 

WITH {
  SELECT DISTINCT ?url ?code ?named_as  ?item WHERE {
    { 
      ?item p:P4652 ?stmt . 
      OPTIONAL {?stmt ps:P4652 ?esco .
        wd:P4652 wdt:P1630 ?fmt .
        BIND(IRI(REPLACE(?esco, '(^.*)', ?fmt)) AS ?esco_url) .  
        OPTIONAL {?stmt pq:P528 ?code} .
      }
      OPTIONAL {?stmt pq:P4900/p:P4652/pq:P528 ?c1 . BIND(CONCAT(?c1, ".x") AS ?code)} .
    } UNION {
      ?item wdt:P31 wd:Q108300140 . 
      ?item p:P8283 ?stmt .
      ?stmt ps:P8283 ?code .
      wd:P8283 wdt:P1630 ?fmt .
      BIND(IRI(REPLACE(?code, '(^.*)', ?fmt)) AS ?isco_url) .
    } .
    OPTIONAL {?stmt pq:P1810 ?named_as} .
    BIND(COALESCE(?isco_url, ?esco_url) AS ?url) .
  }
} AS %items 

WHERE {
  INCLUDE %items .
  ?item wikibase:statements ?stmts . 
  ?item wikibase:sitelinks  ?links . 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "[AUTO_LANGUAGE],en". }
} ORDER BY ?code ?named_as ?itemLabel

