SELECT ?partof ?itemLabel ?count ?Cameron ?May ?Johnson ?Truss
WITH {
SELECT ?partof ?C WHERE {
  ?article schema:about wd:Q192;
  schema:isPartOf ?partof.
  ?partof wikibase:wikiGroup "wikipedia" .
  BIND ("Cameron" as ?C)
} } as %i 
WITH {
SELECT ?partof ?M WHERE {
  ?article schema:about wd:Q264766;
  schema:isPartOf ?partof.
  ?partof wikibase:wikiGroup "wikipedia" .
  BIND ("May" as ?M)
} } as %j
WITH {
SELECT ?partof ?J WHERE {
  ?article schema:about wd:Q180589;
  schema:isPartOf ?partof.
  ?partof wikibase:wikiGroup "wikipedia" .
  BIND ("Johnson" as ?J)
} } as %k
WITH {
SELECT ?partof ?T WHERE {
  ?article schema:about wd:Q272201;
  schema:isPartOf ?partof.
  ?partof wikibase:wikiGroup "wikipedia" .
  BIND ("Truss" as ?T)
} } as %l
WITH {
  SELECT ?partof ?itemLabel (count(*) as ?count) (SAMPLE(?C) as ?Cameron) (SAMPLE(?M) as ?May) (SAMPLE (?J) as ?Johnson) (SAMPLE (?T) as ?Truss) WHERE
{
  {INCLUDE %i}
  UNION
  {INCLUDE %j}
  UNION
  {INCLUDE %k}
  UNION
  {INCLUDE %l}
} group by ?partof ?itemLabel } as %m
WHERE
{
  INCLUDE %m
  ?item wdt:P856 ?partof. 
  ?item wdt:P31 wd:Q10876391 .
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". } 
}

