SELECT ?item (SAMPLE(?coords) AS ?coord) ?itemLabel ?out WITH {
  SELECT DISTINCT ?out ?depth WHERE {
    SERVICE <https://query.wikidata.org/bigdata/namespace/categories/sparql> {
      SERVICE mediawiki:categoryTree {
        bd:serviceParam mediawiki:start <https://commons.wikimedia.org/wiki/Category:Villages_in_Saale-Holzland-Kreis> .
        bd:serviceParam mediawiki:direction 'Reverse' .
        bd:serviceParam mediawiki:depth 1 .  # direct subcategories only
      }
    }
  }
} AS %subquery WHERE {
  INCLUDE %subquery .
  FILTER(?depth = 1) .  # remove the host category from results
  OPTIONAL {
    ?out schema:about/wdt:P301? ?item .
    FILTER NOT EXISTS { ?item wdt:P31 wd:Q4167836 }  # remove category items from results set
    OPTIONAL {
      ?item wdt:P625 ?coords .
    }
  }
  SERVICE wikibase:label { bd:serviceParam wikibase:language 'de' }
} GROUP BY ?item ?itemLabel ?out # to aggregate sample coordinates in case there are multiple P625 values in items

SELECT ?project ?total_redirects ?intentional_redirects ?pct_intentional  ?other_redirects ?pct_other
WHERE {
  {
    SELECT ?project (COUNT(DISTINCT ?intentional_sitelink) AS ?intentional_redirects) (COUNT(DISTINCT ?other_sitelink) AS ?other_redirects) WHERE {
      {
        ?intentional_sitelink schema:isPartOf ?project; wikibase:badge wd:Q70894304 .
      } UNION {
        ?other_sitelink schema:isPartOf ?project; wikibase:badge wd:Q70893996 .
      }
    } GROUP BY ?project 
  }
  BIND (?intentional_redirects + ?other_redirects AS ?total_redirects) .
  BIND ((ROUND(1000.0 * ?intentional_redirects / ?total_redirects) / 10.0) AS ?pct_intentional) .
  BIND ((ROUND(1000.0 * ?other_redirects / ?total_redirects) / 10.0) AS ?pct_other) .
} ORDER BY DESC(?total_redirects)

SELECT DISTINCT ?airport ?airportLabel ?airportCountry ?airportCountryLabel ?person ?personLabel (sample(?birth) as ?birth) (SAMPLE(?death) as ?death) WHERE {
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  ?airport (wdt:P31/(wdt:P279*)) wd:Q1248784;
    wdt:P17 ?airportCountry;
    p:P138 ?statement.
  ?statement ps:P138 ?person.
  ?person wdt:P31 wd:Q5;
    wdt:P106 wd:Q49757.
  OPTIONAL { ?person wdt:P569 ?birth }
  OPTIONAL { ?person wdt:P570 ?death }
  FILTER(NOT EXISTS { ?statement pq:P582 ?endTime. })
} group by ?airport ?airportLabel ?airportCountry ?airportCountryLabel ?person ?personLabel ?personImage

