  ?loc wdt:P131 wd:Q647 ;
       wdt:P138 ?item ;
       wdt:P625 ?coordRennes .
  ?item wdt:P31 ?type ; wdt:P625 ?coord .
  BIND(ROUND(geof:distance(?coordRennes, ?coord)*10)/10 AS ?dist ).
  BIND( IF( ?dist < 400 , "FBB806", IF ( ?dist < 1000 , "DE4710" , IF ( ?dist < 2000 , "DE4710" , "940E0E" ) ) ) AS ?rgb).  
