# Source https://www.wikidata.org/wiki/Wikidata:SPARQL_query_service/qotw/2022#March "Shortest rail link between Narvik and Singapore"
#defaultView:Map
SELECT ?station ?stationLabel ?cds ?line ?layer WHERE { 
  { SERVICE gas:service {
       gas:program gas:gasClass "com.bigdata.rdf.graph.analytics.BFS" ; 
                   gas:in wd:Q3374603 ; # Umeå central station
                  # gas:target wd:Q18337797 ; # Didymoteicho railway station. From here on, adjacent stations rapidly vanish
                   gas:out ?station ; 
                   gas:out1 [] ; 
                   gas:out2 ?pred .
       gas:program gas:linkType wdt:P197 . } # adjacent station
  }
  FILTER(?station != wd:Q3374603) 
  ?pred p:P625/psv:P625/wikibase:geoLatitude ?lat1 ; 
        p:P625/psv:P625/wikibase:geoLongitude ?lon1 .
  ?station p:P625/psv:P625/wikibase:geoLatitude ?lat2 ; 
           p:P625/psv:P625/wikibase:geoLongitude ?lon2 .
  BIND(CONCAT('LINESTRING(', STR(?lon1), ' ', STR(?lat1), ',', STR(?lon2), ' ', STR(?lat2), ')') AS ?str) . 
  BIND(STRDT(?str, geo:wktLiteral) AS ?line) 
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
}

